        gpy_max =( i_stamone.bin_y_1 );                          // [---][210]
                                                                 // [---][210]
        gpx_wid =( gpx_max - gpx_min + 1 );                      // [---][210]
        gpy_hig =( gpy_max - gpy_min + 1 );                      // [---][210]
                                                                 // [---][210]
        let strider =( i_stamone.bin_wid );                      // [---][210]
        let bin_wid =( i_stamone.bin_wid );                      // [---][210]
